localStorage.setItem("analyticsCookies",false);localStorage.setItem("targetingCookies",false);var delete_cookie=function(name){document.cookie=name+'=;path=/;expires=Thu, 01 Jan 1970 00:00:01 GMT;';};const removeCookies=()=>{let cookies=document.cookie;let ca=cookies.split(";");for(let i=0;i<ca.length;i++){let key=ca[i].split("=");document.cookie=`${key[0]}="";expires=Thu, 01 Jan 1970 00:00:00 UTC;path=/;`;}}
