$('.reject_all_cookies').on('click',function(){localStorage.setItem("analyticsCookies",false);localStorage.setItem("targetingCookies",false);localStorage.setItem("consentcook",9);var days=7;var date=new Date();var res=date.setTime(date.getTime()+(days*24*60*60*1000));localStorage.setItem("consentcooktime",res);setCookie('analyticsCookies',false,100);setCookie('targetingCookies',false,100);removeCookies();window.location.reload();})